#include <stdio.h>
#include <locale.h>
#include <Windows.h>
int main(void)
{
	setlocale(LC_ALL, "rus");

	printf_s("_____________________________________________________________________________________________________________________________\n");
	printf_s("|  Type:  |   Size:  |         MAX(10)        ||         MAX(16)        ||         MIN(10)        ||         MIN(16)        |\n");
	printf_s("|---------+----------+------------------------++------------------------++------------------------++------------------------|\n");
	printf_s("|  Char:  |   %d      |            %d         ||           %#x         ||          %d          ||         %#x     |\n", sizeof(CHAR), CHAR_MAX, CHAR_MAX, CHAR_MIN, CHAR_MIN);
	printf_s("|---------+----------+------------------------++------------------------++------------------------++------------------------|\n");
	printf_s("|  Byte:  |   %d      |            %d         ||           %#x         ||          0             ||         0x00000000     |\n", sizeof(BYTE), UCHAR_MAX, UCHAR_MAX);
	printf_s("|---------+----------+------------------------++------------------------++------------------------++------------------------|\n");
	printf_s("|  Int:   |   %d      |        %d      ||        %#x      ||      %d       ||         %#x     |\n", sizeof(INT), INT_MAX, INT_MAX, INT_MIN, INT_MIN);
	printf_s("|---------+----------+------------------------++------------------------++------------------------++------------------------|\n");
	printf_s("|  Dword: |   %d      |        4294967293      ||        0xfffffffd‬     ||          0             ||         0x00000000     |\n", sizeof(INT));
	printf_s("|---------+----------+------------------------++------------------------++------------------------++------------------------|\n");
	printf_s("|  Long:  |   %d      |        %d      ||        %#x      ||      %d       ||         %#x     |\n", sizeof(LONG), LONG_MAX, LONG_MAX, LONG_MIN, LONG_MIN);
	printf_s("|---------+----------+------------------------++------------------------++------------------------++------------------------|\n");
	printf_s("|  Uint:  |   %d      |        4294967293      ||        0xfffffffd‬     ||          0             ||         0x00000000     |\n", sizeof(INT));
	printf_s("|---------+----------+------------------------++------------------------++------------------------++------------------------|\n");
	printf_s("|  Null:  |   %d      |                                          Нулевой указатель!                                          |\n", sizeof(NULL));
	printf_s("|---------+----------+------------------------++------------------------++------------------------++------------------------|\n");
	printf_s("|  Bool:  |   %d      |                                               True/False                                             |\n", sizeof(BOOL));
	printf_s("|---------+----------+------------------------++------------------------++------------------------++------------------------|\n");
	printf_s("\n");
	return 0;
}